import "./App.css";
import { useState } from "react";
import axios from "axios";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import CreateMemo from "./Components/CreateMemo";
import MemoList from "./Components/MemoList";
const headAddress = "http://localhost:3001";
function App() {
  const test = async function () {
    // console.log(test1);
    //서버의 res를 testAxios1 에 담기.
    let testAxios1 = await axios({
      //token
      method: "GET",
      url: `${headAddress}/text`,
      params: {
        name: "hotelName",
      },
    });
    //서버에서 받아온 정보 콘솔
    console.log("testAxios1.datat", testAxios1.data);
    //test1 state 변화 콘솔
    console.log("test1", test1);
    //test1 state에 받아온 정보 추가;
    setTest([...test1, testAxios1.data]);
    return testAxios1;
  }; //end test fun

  let localMemo = JSON.parse(localStorage.getItem("memo"));
  const [memoData, setMemoData] = useState(localMemo);

  if (localMemo == null) {
    setMemoData([]);
  }

  localStorage.setItem("memo", JSON.stringify(memoData));

  return (
    <div className="mainpage">
      <center>
        <CreateMemo memoData={memoData} setMemoData={setMemoData}>
          {" "}
        </CreateMemo>
        <h1> MEMOLIST </h1>
        <Router>
          <Switch>
            <Route
              path="/"
              exact
              component={() => <MemoList memoData={memoData} />}
            ></Route>
            {/* <Route
            path="/CreateMemo"
            exact
            component={() => (
              <CreateMemo memoData={memoData} setMemoData={setMemoData} />
            )}
          ></Route> */}
          </Switch>
        </Router>
      </center>
    </div>
  );
}

export default App;

