import { Component, useState, Fragment } from "react";
import "./App.css";
// import CreateMemoMemo from "./Components/CreateMmo";
// import MemoList from "./Components/MemoList";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import CreateMemo from "./Components/CreateMemo";

function App() {
  let localMemo = JSON.parse(localStorage.getItem("memo"));
  const [memoData, setMemoData] = useState(localMemo);

  if (localMemo == null) {
    setMemoData([]);
  }

  localStorage.setItem("memo", JSON.stringify(memoData));

  return (
    <div>
      <center>
        <div>
          <h1>Memo List</h1>
          {/* <Router>
            <Link to="/MemoList">
              <h2> Memo List </h2>
            </Link>
          </Router> */}
          <h1>Create Memo</h1>
          <Router>
            <Route
              path="/CreateMemo"
              exact
              // component={() => (
              //   <CreateMemo memoData={memoData} setMemoData={setMemoData} />
              // )}
            >
              <Link to="/CreateMemo">
                <h2> Create memo </h2>
              </Link>
            </Route>
          </Router>
        </div>
      </center>

      <Router>
        {/* <Route
          path="/MemoList"
          exact
          component={() => (
            <CreateMemo memoData={memoData} setMemoData={setMemoData} />
          )}
        ></Route> */}
        <Route
          path="/CreateMemo"
          exact
          component={() => (
            <CreateMemo memoData={memoData} setMemoData={setMemoData} />
          )}
        ></Route>
      </Router>
    </div>
  );
}

export default App;

