import React from "react";
import { BiSave } from "react-icons/bi";
import { Link } from "react-router-dom";
import { useState } from "react";

function CreateMemo(props) {
  let idx = 0;
  if (localStorage.getItem("memo")) {
    let memoidx = JSON.parse(localStorage.getItem("memo")).length;
    // console.log(memoidx);
    // console.log(memoidx);
    idx = memoidx;
  }

  const [title, setTitle] = useState();
  const [content, setContent] = useState();
  function inputTitle(e) {
    setTitle(e.target.value);
  }

  function inputContent(e) {
    setContent(e.target.value);
  }

  function addMemo() {
    props.setMemoData([
      ...props.MemoData,
      {
        idx: idx,
        title: title,
        content: content,
      },
    ]);
  }

  return (
    <div>
      <input className="inputTitle" onChange={inputTitle} />
      <textarea onChange={inputContent} />
    </div>
  );
}

export default CreateMemo;
