import "./App.css";
import { Component, useState, Fragment } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import CreateMemo from "./Components/CreateMemo";
import MemoList from "./Components/MemoList";

function App() {
  let localMemo = JSON.parse(localStorage.getItem("memo"));
  const [memoData, setMemoData] = useState(localMemo);

  if (localMemo == null) {
    setMemoData([]);
  }

  localStorage.setItem("memo", JSON.stringify(memoData));

  return (
    <div className="mainpage">
      <CreateMemo> </CreateMemo>
      <Router>
        <Switch>
          <Route
            path="/"
            exact
            component={() => <MemoList memoData={memoData} />}
          ></Route>
          <Route
            path="/CreateMemo"
            exact
            component={() => (
              <CreateMemo memoData={memoData} setMemoData={setMemoData} />
            )}
          ></Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;

