import React from "react";
// import { BiSave } from "react-icons/bi";
// import { Link } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
const headAddress = "http://localhost:3001";

function CreateMemo(props) {
  //   let idx = 0;
  //   if (localStorage.getItem("memo")) {
  //     let memoidx = JSON.parse(localStorage.getItem("memo")).length;
  //     // console.log(memoidx);
  //     // console.log(memoidx);
  //     idx = memoidx;
  //   }

  const insert = async function () {
    let testAxios1 = await axios({
      method: "GET",
      url: `${headAddress}/insert`,
      params: {
        title: title,
        content: content,
      },
    });
    //서버에서 받아온 정보 콘솔
    console.log("title", title, "content", content);
    console.log("axios", testAxios1);
    return testAxios1;
  };

  const [title, setTitle] = useState();
  const [content, setContent] = useState();
  function inputTitle(e) {
    setTitle(e.target.value);
  }

  function inputContent(e) {
    setContent(e.target.value);
  }

  function addMemo() {
    props.setMemoData([
      ...props.memoData,
      {
        // idx: idx,
        title: title,
        content: content,
      },
    ]);
    console.log("111111");
  }

  return (
    <div>
      <center>
        <h1> CreateMemo </h1>
        <input onChange={inputTitle} placeholder="TITLE" />
        <br />
        <textarea onChange={inputContent} placeholder="CONTENT" />
        <br />

        <button className="saveBtn" onClick={insert}>
          SAVE
        </button>
      </center>
    </div>
  );
}

export default CreateMemo;
